// Advanced Risk Analysis Engine for Alpaca Trading
const positions = $input.first().json || [];
const account = $input.all()[1].json;
const orders = $input.all()[2].json || [];

// Account metrics
const equity = parseFloat(account.equity);
const buyingPower = parseFloat(account.buying_power);
const cash = parseFloat(account.cash);
const portfolioValue = parseFloat(account.portfolio_value);
const daytradeCount = parseInt(account.daytrade_buying_power);

// Position analysis
let totalPositionValue = 0;
let totalUnrealizedPnL = 0;
let totalMarketValue = 0;
let leverageUsed = 0;

const positionDetails = positions.map(position => {
  const marketValue = parseFloat(position.market_value);
  const unrealizedPnL = parseFloat(position.unrealized_pl);
  const costBasis = parseFloat(position.cost_basis);
  const qty = parseFloat(position.qty);
  const side = position.side;

  totalPositionValue += Math.abs(costBasis);
  totalUnrealizedPnL += unrealizedPnL;
  totalMarketValue += Math.abs(marketValue);

  return {
    symbol: position.symbol,
    side: side,
    qty: qty,
    marketValue: marketValue,
    costBasis: costBasis,
    unrealizedPnL: unrealizedPnL,
    unrealizedPlPercent: parseFloat(position.unrealized_plpc) * 100,
    currentPrice: parseFloat(position.current_price),
    avgEntryPrice: parseFloat(position.avg_entry_price),
    exposure: Math.abs(marketValue) / equity * 100
  };
});

// Risk calculations
const portfolioUtilization = (totalMarketValue / equity) * 100;
const cashUtilization = ((equity - cash) / equity) * 100;
const unrealizedPnLPercent = (totalUnrealizedPnL / equity) * 100;
const largestPosition = Math.max(...positionDetails.map(p => Math.abs(p.marketValue)), 0);
const positionConcentration = equity > 0 ? (largestPosition / equity) * 100 : 0;

// Order analysis for trading behavior
const recentOrders = orders.slice(0, 10);
const filledOrders = recentOrders.filter(order => order.status === 'filled');
const cancelledOrders = recentOrders.filter(order => order.status === 'cancelled');
const rejectedOrders = recentOrders.filter(order => order.status === 'rejected');

// Trading frequency and behavior metrics
const todayOrders = orders.filter(order => {
  const orderDate = new Date(order.created_at).toDateString();
  const today = new Date().toDateString();
  return orderDate === today;
});

// Risk level determination
const RISK_THRESHOLDS = {
  LOW: { pnl: 5, concentration: 25, utilization: 50 },
  MEDIUM: { pnl: 10, concentration: 40, utilization: 70 },
  HIGH: { pnl: 15, concentration: 60, utilization: 85 },
  CRITICAL: { pnl: 20, concentration: 80, utilization: 95 }
};

let riskLevel = 'LOW';
let riskColor = 'green';
let alertMessage = '';
let emergencyStop = false;

// Determine risk level based on multiple factors
const riskFactors = {
  pnlRisk: Math.abs(unrealizedPnLPercent),
  concentrationRisk: positionConcentration,
  utilizationRisk: portfolioUtilization
};

if (riskFactors.pnlRisk > RISK_THRESHOLDS.CRITICAL.pnl || 
    riskFactors.concentrationRisk > RISK_THRESHOLDS.CRITICAL.concentration ||
    riskFactors.utilizationRisk > RISK_THRESHOLDS.CRITICAL.utilization) {
  riskLevel = 'CRITICAL';
  riskColor = 'red';
  emergencyStop = true;
  alertMessage = 'ğŸš¨ CRITICAL RISK: Immediate action required!';
} else if (riskFactors.pnlRisk > RISK_THRESHOLDS.HIGH.pnl || 
           riskFactors.concentrationRisk > RISK_THRESHOLDS.HIGH.concentration ||
           riskFactors.utilizationRisk > RISK_THRESHOLDS.HIGH.utilization) {
  riskLevel = 'HIGH';
  riskColor = 'orange';
  alertMessage = 'âš ï¸ HIGH RISK: Consider reducing positions';
} else if (riskFactors.pnlRisk > RISK_THRESHOLDS.MEDIUM.pnl || 
           riskFactors.concentrationRisk > RISK_THRESHOLDS.MEDIUM.concentration ||
           riskFactors.utilizationRisk > RISK_THRESHOLDS.MEDIUM.utilization) {
  riskLevel = 'MEDIUM';
  riskColor = 'yellow';
  alertMessage = 'ğŸ’› MEDIUM RISK: Monitor closely';
}

// Additional risk checks
const isDayTradingLimit = daytradeCount >= 3; // PDT rule concern
const highOrderRejectionRate = recentOrders.length > 0 ? (rejectedOrders.length / recentOrders.length) > 0.2 : false;
const excessiveTradingToday = todayOrders.length > 10;

if (isDayTradingLimit) {
  alertMessage += ' | Day trading limit concern';
}
if (highOrderRejectionRate) {
  alertMessage += ' | High order rejection rate';
}
if (excessiveTradingToday) {
  alertMessage += ' | Excessive trading activity today';
}

// Performance metrics
const totalReturn = equity - 100000; // Assuming $100k starting capital
const totalReturnPercent = (totalReturn / 100000) * 100;
const winningPositions = positionDetails.filter(p => p.unrealizedPnL > 0).length;
const losingPositions = positionDetails.filter(p => p.unrealizedPnL < 0).length;
const winRate = positions.length > 0 ? (winningPositions / positions.length) * 100 : 0;

return [{
  json: {
    riskAnalysis: {
      timestamp: new Date().toISOString(),
      riskLevel,
      riskColor,
      emergencyStop,
      alertMessage,
      shouldAlert: riskLevel !== 'LOW' || isDayTradingLimit || highOrderRejectionRate || excessiveTradingToday,

      // Account metrics
      account: {
        equity: parseFloat(equity.toFixed(2)),
        buyingPower: parseFloat(buyingPower.toFixed(2)),
        cash: parseFloat(cash.toFixed(2)),
        portfolioValue: parseFloat(portfolioValue.toFixed(2)),
        totalUnrealizedPnL: parseFloat(totalUnrealizedPnL.toFixed(2)),
        unrealizedPnLPercent: parseFloat(unrealizedPnLPercent.toFixed(2)),
        totalReturn: parseFloat(totalReturn.toFixed(2)),
        totalReturnPercent: parseFloat(totalReturnPercent.toFixed(2))
      },

      // Risk metrics
      risk: {
        portfolioUtilization: parseFloat(portfolioUtilization.toFixed(2)),
        cashUtilization: parseFloat(cashUtilization.toFixed(2)),
        positionConcentration: parseFloat(positionConcentration.toFixed(2)),
        largestPositionValue: parseFloat(largestPosition.toFixed(2)),
        positionCount: positions.length,
        maxPositions: 10, // Conservative limit
        riskFactors
      },

      // Trading metrics
      trading: {
        todayOrderCount: todayOrders.length,
        recentOrderCount: recentOrders.length,
        filledOrderCount: filledOrders.length,
        rejectedOrderCount: rejectedOrders.length,
        orderSuccessRate: recentOrders.length > 0 ? ((filledOrders.length / recentOrders.length) * 100).toFixed(1) : 0,
        isDayTradingLimit,
        winRate: parseFloat(winRate.toFixed(1)),
        winningPositions,
        losingPositions
      },

      // Detailed positions
      positions: positionDetails
    }
  }
}];

