// Comprehensive Risk Report Generator
const riskData = $input.first().json.riskAnalysis;
const timestamp = new Date().toISOString();

// Generate detailed risk assessment
const riskScore = (
  (riskData.risk.portfolioUtilization * 0.3) +
  (riskData.risk.positionConcentration * 0.3) +
  (Math.abs(riskData.account.unrealizedPnLPercent) * 0.4)
);

const report = {
  reportId: `alpaca-risk-${Date.now()}`,
  timestamp,
  accountSummary: {
    brokerage: 'Alpaca Markets',
    accountType: 'Paper Trading',
    equity: riskData.account.equity,
    buyingPower: riskData.account.buyingPower,
    cash: riskData.account.cash,
    unrealizedPnL: riskData.account.totalUnrealizedPnL,
    totalReturn: riskData.account.totalReturn,
    returnPercentage: riskData.account.totalReturnPercent
  },
  riskAssessment: {
    overallRiskLevel: riskData.riskLevel,
    riskScore: Math.round(riskScore),
    emergencyStatus: riskData.emergencyStop,
    primaryConcerns: riskData.alertMessage ? riskData.alertMessage.split('|').map(s => s.trim()) : []
  },
  positionAnalysis: {
    totalPositions: riskData.risk.positionCount,
    portfolioUtilization: riskData.risk.portfolioUtilization,
    concentrationRisk: riskData.risk.positionConcentration,
    largestPosition: riskData.risk.largestPositionValue,
    winningPositions: riskData.trading.winningPositions,
    losingPositions: riskData.trading.losingPositions,
    winRate: riskData.trading.winRate
  },
  tradingBehavior: {
    todayOrders: riskData.trading.todayOrderCount,
    recentOrders: riskData.trading.recentOrderCount,
    successRate: parseFloat(riskData.trading.orderSuccessRate),
    rejectedOrders: riskData.trading.rejectedOrderCount,
    dayTradingLimitRisk: riskData.trading.isDayTradingLimit
  },
  recommendations: [
    riskData.emergencyStop ? 'ğŸš¨ IMMEDIATE: Stop all trading and close positions' : null,
    riskData.risk.portfolioUtilization > 80 ? 'âš–ï¸ Reduce portfolio utilization' : null,
    riskData.risk.positionConcentration > 50 ? 'ğŸ“Š Diversify position allocation' : null,
    riskData.trading.winRate < 40 && riskData.risk.positionCount > 2 ? 'ğŸ“ˆ Review trading strategy performance' : null,
    riskData.trading.orderSuccessRate < 80 ? 'ğŸ”§ Optimize order execution strategy' : null,
    riskData.trading.isDayTradingLimit ? 'âš ï¸ Monitor day trading limits (PDT rule)' : null,
    riskData.account.unrealizedPnLPercent < -10 ? 'ğŸ›¡ï¸ Implement stricter stop losses' : null,
    'ğŸ’¡ Continue systematic risk monitoring'
  ].filter(Boolean),
  topPositions: riskData.positions.slice(0, 5).map(pos => ({
    symbol: pos.symbol,
    side: pos.side,
    marketValue: pos.marketValue,
    unrealizedPnL: pos.unrealizedPnL,
    exposure: pos.exposure.toFixed(1) + '%'
  }))
};

return [{ json: { riskReport: report, rawData: riskData } }];
