// Portfolio Performance Analytics Engine
const positions = $input.first().json.positions || [];
const stats = $input.all()[1].json.stats;
const history = $input.all()[2].json.history || [];
const timestamp = new Date().toISOString();

// Account constants
const INITIAL_CAPITAL = 50000;
const CURRENT_BALANCE = 50000; // This would come from actual balance API

// Calculate current portfolio metrics
let totalPositionValue = 0;
let totalUnrealizedPnL = 0;
let leverageUsed = 0;

const positionDetails = positions.map(position => {
  // Mock current prices (in real implementation, fetch from API)
  const currentPrice = position.entryPrice * (1 + (Math.random() - 0.5) * 0.03);
  const positionValue = position.quantity * position.entryPrice;
  const unrealizedPnL = position.side === 'LONG' 
    ? (currentPrice - position.entryPrice) * position.quantity
    : (position.entryPrice - currentPrice) * position.quantity;
  const pnlPercentage = (unrealizedPnL / positionValue) * 100;

  totalPositionValue += positionValue;
  totalUnrealizedPnL += unrealizedPnL;
  leverageUsed += positionValue * (position.leverage || 1);

  return {
    symbol: position.symbol,
    side: position.side,
    entryPrice: position.entryPrice,
    currentPrice: parseFloat(currentPrice.toFixed(2)),
    quantity: position.quantity,
    positionValue: parseFloat(positionValue.toFixed(2)),
    unrealizedPnL: parseFloat(unrealizedPnL.toFixed(2)),
    pnlPercentage: parseFloat(pnlPercentage.toFixed(2)),
    leverage: position.leverage || 1,
    duration: position.openTime ? 
      Math.floor((Date.now() - new Date(position.openTime).getTime()) / (1000 * 60 * 60)) + 'h' : 'N/A'
  };
});

// Calculate portfolio-level metrics
const totalEquity = CURRENT_BALANCE + totalUnrealizedPnL;
const totalReturn = totalEquity - INITIAL_CAPITAL;
const totalReturnPercentage = (totalReturn / INITIAL_CAPITAL) * 100;
const leverageRatio = leverageUsed / CURRENT_BALANCE;

// Risk metrics
const largestPosition = Math.max(...positionDetails.map(p => Math.abs(p.positionValue)), 0);
const positionConcentration = totalPositionValue > 0 ? (largestPosition / totalPositionValue) * 100 : 0;
const portfolioVolatility = Math.sqrt(positionDetails.reduce((sum, p) => sum + Math.pow(p.pnlPercentage, 2), 0) / Math.max(positionDetails.length, 1));

// Performance metrics from trading history
const recentTrades = history.slice(0, 10);
const winningTrades = recentTrades.filter(trade => trade.result && trade.result.includes('profit')).length;
const winRate = recentTrades.length > 0 ? (winningTrades / recentTrades.length) * 100 : 0;

// Daily/Weekly performance tracking
const today = new Date();
const weekAgo = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
const todayTrades = history.filter(trade => 
  new Date(trade.timestamp) >= new Date(today.toDateString())
);
const weekTrades = history.filter(trade => 
  new Date(trade.timestamp) >= weekAgo
);

// Calculate Sharpe ratio (simplified)
const avgDailyReturn = totalReturnPercentage / 30; // Assuming 30 days
const riskFreeRate = 0.02; // 2% annual risk-free rate
const sharpeRatio = portfolioVolatility > 0 ? (avgDailyReturn - riskFreeRate/365) / (portfolioVolatility/100) : 0;

// Generate performance report
const portfolioMetrics = {
  timestamp,
  accountSummary: {
    initialCapital: INITIAL_CAPITAL,
    currentBalance: CURRENT_BALANCE,
    totalPositionValue: parseFloat(totalPositionValue.toFixed(2)),
    totalUnrealizedPnL: parseFloat(totalUnrealizedPnL.toFixed(2)),
    totalEquity: parseFloat(totalEquity.toFixed(2)),
    totalReturn: parseFloat(totalReturn.toFixed(2)),
    totalReturnPercentage: parseFloat(totalReturnPercentage.toFixed(2)),
    availableBalance: parseFloat((CURRENT_BALANCE - totalPositionValue).toFixed(2))
  },
  riskMetrics: {
    leverageRatio: parseFloat(leverageRatio.toFixed(2)),
    positionCount: positions.length,
    positionConcentration: parseFloat(positionConcentration.toFixed(2)),
    portfolioVolatility: parseFloat(portfolioVolatility.toFixed(2)),
    largestPositionValue: parseFloat(largestPosition.toFixed(2)),
    riskLevel: leverageRatio > 3 ? 'HIGH' : leverageRatio > 1.5 ? 'MEDIUM' : 'LOW'
  },
  performanceMetrics: {
    winRate: parseFloat(winRate.toFixed(1)),
    totalTrades: history.length,
    recentTradesCount: recentTrades.length,
    todayTrades: todayTrades.length,
    weekTrades: weekTrades.length,
    sharpeRatio: parseFloat(sharpeRatio.toFixed(3)),
    avgTradeSize: recentTrades.length > 0 ? 
      parseFloat((recentTrades.reduce((sum, trade) => sum + (trade.capital || 0), 0) / recentTrades.length).toFixed(2)) : 0
  },
  positions: positionDetails,
  alerts: {
    highRisk: leverageRatio > 3,
    lowWinRate: winRate < 40 && recentTrades.length >= 5,
    concentrationRisk: positionConcentration > 50,
    largeDrawdown: totalReturnPercentage < -10,
    unusualVolatility: portfolioVolatility > 15
  },
  recommendations: {
    action: totalReturnPercentage > 5 ? 'MAINTAIN_STRATEGY' : 
           totalReturnPercentage < -5 ? 'REVIEW_STRATEGY' : 'MONITOR_CLOSELY',
    priority: leverageRatio > 3 || totalReturnPercentage < -10 ? 'HIGH' : 
             leverageRatio > 1.5 || totalReturnPercentage < -5 ? 'MEDIUM' : 'LOW',
    suggestions: [
      leverageRatio > 3 ? 'Reduce leverage exposure' : null,
      positionConcentration > 50 ? 'Diversify position allocation' : null,
      winRate < 40 && recentTrades.length >= 5 ? 'Review trading strategy' : null,
      totalReturnPercentage < -10 ? 'Consider risk management review' : null,
      totalReturnPercentage > 10 ? 'Consider taking some profits' : null
    ].filter(Boolean)
  }
};

return [{ json: { portfolioAnalysis: portfolioMetrics } }];

// Generate Daily Performance Report
const analysis = $input.first().json.portfolioAnalysis;
const timestamp = new Date().toISOString();
const date = new Date().toDateString();

// Performance summary
const summary = {
  reportDate: date,
  reportTime: timestamp,
  accountOverview: {
    totalEquity: `$${analysis.accountSummary.totalEquity.toLocaleString()}`,
    totalReturn: `${analysis.accountSummary.totalReturnPercentage >= 0 ? '+' : ''}${analysis.accountSummary.totalReturnPercentage}%`,
    dailyPnL: `$${analysis.accountSummary.totalUnrealizedPnL.toLocaleString()}`,
    availableBalance: `$${analysis.accountSummary.availableBalance.toLocaleString()}`
  },
  tradingActivity: {
    activePositions: analysis.riskMetrics.positionCount,
    leverageUsed: `${analysis.riskMetrics.leverageRatio}x`,
    winRate: `${analysis.performanceMetrics.winRate}%`,
    totalTrades: analysis.performanceMetrics.totalTrades,
    todayTrades: analysis.performanceMetrics.todayTrades
  },
  riskAssessment: {
    riskLevel: analysis.riskMetrics.riskLevel,
    portfolioVolatility: `${analysis.riskMetrics.portfolioVolatility}%`,
    positionConcentration: `${analysis.riskMetrics.positionConcentration}%`,
    sharpeRatio: analysis.performanceMetrics.sharpeRatio
  },
  topPositions: analysis.positions.slice(0, 5).map(pos => ({
    symbol: pos.symbol.toUpperCase(),
    side: pos.side,
    value: `$${pos.positionValue.toLocaleString()}`,
    pnl: `${pos.pnlPercentage >= 0 ? '+' : ''}${pos.pnlPercentage}%`,
    duration: pos.duration
  })),
  alerts: Object.keys(analysis.alerts).filter(key => analysis.alerts[key]),
  recommendations: analysis.recommendations.suggestions,
  marketInsights: {
    recommendedAction: analysis.recommendations.action,
    priority: analysis.recommendations.priority,
    nextReview: new Date(Date.now() + 2 * 60 * 60 * 1000).toISOString() // 2 hours from now
  }
};

// Generate performance grade
const gradeFactors = [
  analysis.accountSummary.totalReturnPercentage > 0 ? 25 : 0,
  analysis.performanceMetrics.winRate > 50 ? 25 : analysis.performanceMetrics.winRate * 0.5,
  analysis.riskMetrics.leverageRatio < 2 ? 25 : Math.max(0, 25 - (analysis.riskMetrics.leverageRatio - 2) * 10),
  analysis.riskMetrics.portfolioVolatility < 10 ? 25 : Math.max(0, 25 - (analysis.riskMetrics.portfolioVolatility - 10) * 2)
];

const performanceScore = gradeFactors.reduce((sum, factor) => sum + factor, 0);
const performanceGrade = performanceScore >= 85 ? 'A' : 
                       performanceScore >= 70 ? 'B' : 
                       performanceScore >= 55 ? 'C' : 
                       performanceScore >= 40 ? 'D' : 'F';

const dailyReport = {
  ...summary,
  performanceGrade: {
    score: Math.round(performanceScore),
    grade: performanceGrade,
    breakdown: {
      returns: gradeFactors[0],
      winRate: Math.round(gradeFactors[1]),
      riskManagement: Math.round(gradeFactors[2]),
      volatility: Math.round(gradeFactors[3])
    }
  }
};

return [{ json: { dailyReport, rawAnalysis: analysis } }];

