// Advanced Notification Formatter
const notification = $input.first().json.body;
const timestamp = new Date().toISOString();
const readableTime = new Date().toLocaleString();

// Priority and urgency mapping
const priorityConfig = {
  HIGH: { emoji: 'ğŸš¨', color: '#FF4444', urgency: 'URGENT' },
  MEDIUM: { emoji: 'âš ï¸', color: '#FFA500', urgency: 'MEDIUM' },
  LOW: { emoji: 'â„¹ï¸', color: '#4CAF50', urgency: 'LOW' },
  CRITICAL: { emoji: 'ğŸ”¥', color: '#8B0000', urgency: 'CRITICAL' }
};

// Type-specific formatting
const typeFormatters = {
  TRADING_OPPORTUNITY: (data) => ({
    title: `${priorityConfig.HIGH.emoji} Trading Opportunity: ${data.symbol?.toUpperCase()}`,
    description: `${data.action} signal detected with ${data.confidence}% confidence`,
    fields: [
      { name: 'Symbol', value: data.symbol?.toUpperCase(), inline: true },
      { name: 'Action', value: data.action, inline: true },
      { name: 'Confidence', value: `${data.confidence}%`, inline: true },
      { name: 'Price', value: `$${data.price}`, inline: true },
      { name: 'Level', value: data.level, inline: true },
      { name: 'Score', value: data.score, inline: true }
    ],
    color: priorityConfig.HIGH.color
  }),

  RISK_ALERT: (data) => ({
    title: `${priorityConfig.CRITICAL.emoji} Risk Management Alert`,
    description: data.message || 'Risk threshold exceeded',
    fields: [
      { name: 'Risk Level', value: data.level, inline: true },
      { name: 'Risk Percentage', value: data.risk, inline: true },
      { name: 'P&L', value: data.pnl, inline: true },
      { name: 'Positions', value: data.positions, inline: true },
      { name: 'Leverage', value: data.leverage, inline: true }
    ],
    color: priorityConfig.CRITICAL.color
  }),

  PORTFOLIO_PERFORMANCE: (data) => ({
    title: `${priorityConfig.MEDIUM.emoji} Portfolio Performance Alert`,
    description: `Portfolio requires attention - ${data.action}`,
    fields: [
      { name: 'Total Return', value: data.totalReturn, inline: true },
      { name: 'Total Equity', value: data.equity, inline: true },
      { name: 'Positions', value: data.positions, inline: true },
      { name: 'Leverage', value: data.leverage, inline: true },
      { name: 'Win Rate', value: data.winRate, inline: true },
      { name: 'Priority', value: data.priority, inline: true }
    ],
    color: data.priority === 'HIGH' ? priorityConfig.HIGH.color : priorityConfig.MEDIUM.color
  }),

  MARKET_INTELLIGENCE: (data) => ({
    title: `${priorityConfig.LOW.emoji} Market Intelligence Update`,
    description: `Market analysis complete - ${data.action}`,
    fields: [
      { name: 'Market Bias', value: data.bias, inline: true },
      { name: 'Strength', value: data.strength, inline: true },
      { name: 'Confidence', value: data.confidence, inline: true },
      { name: 'Opportunities', value: data.opportunities, inline: true },
      { name: 'Signals', value: data.signals, inline: false }
    ],
    color: priorityConfig.LOW.color
  }),

  DAILY_REPORT: (data) => ({
    title: `${priorityConfig.LOW.emoji} Daily Trading Report - Grade ${data.summary?.grade}`,
    description: `Daily performance summary and insights`,
    fields: [
      { name: 'Performance Grade', value: data.summary?.grade, inline: true },
      { name: 'Score', value: data.summary?.score, inline: true },
      { name: 'Total Return', value: data.summary?.return, inline: true },
      { name: 'Equity', value: data.summary?.equity, inline: true },
      { name: 'Win Rate', value: data.summary?.winRate, inline: true },
      { name: 'Active Positions', value: data.summary?.positions, inline: true }
    ],
    color: data.summary?.grade === 'A' ? '#4CAF50' : 
           data.summary?.grade === 'F' ? '#FF4444' : '#FFA500'
  })
};

// Format notification based on type
const formatter = typeFormatters[notification.type];
const formattedNotification = formatter ? formatter(notification) : {
  title: `${priorityConfig.LOW.emoji} Trading Bot Notification`,
  description: 'Unknown notification type',
  fields: [{ name: 'Raw Data', value: JSON.stringify(notification), inline: false }],
  color: priorityConfig.LOW.color
};

// Add common metadata
const finalNotification = {
  ...formattedNotification,
  timestamp: readableTime,
  originalType: notification.type,
  priority: notification.priority || 'LOW',
  urgency: priorityConfig[notification.priority || 'LOW']?.urgency || 'LOW',

  // Slack/Discord webhook format
  embeds: [{
    title: formattedNotification.title,
    description: formattedNotification.description,
    color: parseInt(formattedNotification.color.replace('#', ''), 16),
    fields: formattedNotification.fields,
    footer: {
      text: `AI Trading Bot â€¢ ${readableTime}`,
      icon_url: 'https://cdn-icons-png.flaticon.com/512/2103/2103633.png'
    },
    timestamp: timestamp
  }],

  // Console/Log format
  logMessage: `[${readableTime}] ${notification.type}: ${formattedNotification.description}`,

  // Email format
  emailSubject: formattedNotification.title,
  emailBody: `${formattedNotification.description}\n\n${formattedNotification.fields.map(f => `${f.name}: ${f.value}`).join('\n')}\n\nTimestamp: ${readableTime}`,

  // Dashboard notification format
  dashboardNotification: {
    id: Date.now().toString(),
    type: notification.type,
    title: formattedNotification.title,
    message: formattedNotification.description,
    priority: notification.priority || 'LOW',
    timestamp: timestamp,
    read: false,
    data: notification
  }
};

return [{ json: { notification: finalNotification, original: notification } }];

