// Error Handler and Recovery System
const error = $input.first().json;
const timestamp = new Date().toISOString();

// Categorize error types
const errorCategories = {
  API_ERROR: 'External API failure',
  TRADING_ERROR: 'Trading execution failure',
  DATA_ERROR: 'Data processing failure',
  SYSTEM_ERROR: 'System/Network failure',
  VALIDATION_ERROR: 'Data validation failure'
};

const errorType = error.code?.includes('ECONNREFUSED') ? 'API_ERROR' :
                 error.code?.includes('MODULE_NOT_FOUND') ? 'SYSTEM_ERROR' :
                 error.message?.includes('trading') ? 'TRADING_ERROR' :
                 error.message?.includes('validation') ? 'VALIDATION_ERROR' :
                 'SYSTEM_ERROR';

// Recovery strategies
const recoveryStrategies = {
  API_ERROR: {
    action: 'RETRY_WITH_FALLBACK',
    retryCount: 3,
    retryDelay: 30000, // 30 seconds
    fallbackAction: 'USE_CACHED_DATA'
  },
  TRADING_ERROR: {
    action: 'EMERGENCY_STOP',
    retryCount: 0,
    escalate: true,
    fallbackAction: 'MANUAL_INTERVENTION_REQUIRED'
  },
  DATA_ERROR: {
    action: 'RETRY_WITH_VALIDATION',
    retryCount: 2,
    retryDelay: 10000, // 10 seconds
    fallbackAction: 'SKIP_INVALID_DATA'
  },
  SYSTEM_ERROR: {
    action: 'SYSTEM_RESTART',
    retryCount: 1,
    escalate: true,
    fallbackAction: 'MAINTENANCE_MODE'
  },
  VALIDATION_ERROR: {
    action: 'VALIDATE_AND_CLEAN',
    retryCount: 1,
    fallbackAction: 'USE_DEFAULT_VALUES'
  }
};

const strategy = recoveryStrategies[errorType];

// Generate error report
const errorReport = {
  errorId: `error-${Date.now()}`,
  timestamp,
  errorType,
  category: errorCategories[errorType],
  originalError: {
    message: error.message || 'Unknown error',
    code: error.code || 'NO_CODE',
    stack: error.stack || 'No stack trace',
    context: error.context || 'No context'
  },
  severity: strategy.escalate ? 'CRITICAL' : 'MEDIUM',
  recovery: {
    strategy: strategy.action,
    retryCount: strategy.retryCount,
    retryDelay: strategy.retryDelay,
    fallbackAction: strategy.fallbackAction,
    requiresEscalation: strategy.escalate || false
  },
  impact: {
    tradingAffected: errorType === 'TRADING_ERROR',
    dataAffected: errorType === 'DATA_ERROR' || errorType === 'API_ERROR',
    systemAffected: errorType === 'SYSTEM_ERROR',
    userNotificationRequired: strategy.escalate
  },
  recommendations: [
    strategy.escalate ? 'Immediate attention required' : null,
    errorType === 'API_ERROR' ? 'Check API endpoints and credentials' : null,
    errorType === 'TRADING_ERROR' ? 'Review trading parameters and positions' : null,
    errorType === 'SYSTEM_ERROR' ? 'Check system resources and connectivity' : null,
    'Monitor system stability after recovery'
  ].filter(Boolean)
};

return [{ json: { errorReport, originalError: error } }];

// Notification Analytics and Insights
const notifications = $input.all();
const timestamp = new Date().toISOString();
const timeWindow = 3600000; // 1 hour in milliseconds
const now = Date.now();

// Filter recent notifications (last hour)
const recentNotifications = notifications.filter(n => {
  const notifTime = new Date(n.json.notification?.timestamp || n.json.processedAt).getTime();
  return (now - notifTime) <= timeWindow;
});

// Analyze notification patterns
const analytics = {
  summary: {
    totalNotifications: notifications.length,
    recentNotifications: recentNotifications.length,
    timeWindow: '1 hour',
    analysisTime: timestamp
  },

  byType: recentNotifications.reduce((acc, n) => {
    const type = n.json.notification?.originalType || 'UNKNOWN';
    acc[type] = (acc[type] || 0) + 1;
    return acc;
  }, {}),

  byPriority: recentNotifications.reduce((acc, n) => {
    const priority = n.json.notification?.priority || 'LOW';
    acc[priority] = (acc[priority] || 0) + 1;
    return acc;
  }, {}),

  alerts: {
    highVolumeAlert: recentNotifications.length > 10,
    criticalAlerts: recentNotifications.filter(n => n.json.notification?.priority === 'CRITICAL').length,
    errorAlerts: recentNotifications.filter(n => n.json.errorReport).length,
    tradingAlerts: recentNotifications.filter(n => n.json.notification?.originalType === 'TRADING_OPPORTUNITY').length
  },

  trends: {
    averageNotificationsPerHour: recentNotifications.length,
    mostCommonType: Object.keys(recentNotifications.reduce((acc, n) => {
      const type = n.json.notification?.originalType || 'UNKNOWN';
      acc[type] = (acc[type] || 0) + 1;
      return acc;
    }, {})).reduce((a, b) => 
      (recentNotifications.filter(n => n.json.notification?.originalType === a).length >
       recentNotifications.filter(n => n.json.notification?.originalType === b).length) ? a : b,
      'NONE'
    ),
    criticalRate: recentNotifications.length > 0 ? 
      (recentNotifications.filter(n => n.json.notification?.priority === 'CRITICAL').length / recentNotifications.length * 100).toFixed(1) + '%' : '0%'
  },

  recommendations: [
    recentNotifications.length > 15 ? 'âš ï¸ High notification volume - check for issues' : null,
    recentNotifications.filter(n => n.json.notification?.priority === 'CRITICAL').length > 3 ? 'ğŸš¨ Multiple critical alerts - immediate attention required' : null,
    recentNotifications.filter(n => n.json.errorReport).length > 2 ? 'ğŸ”§ Multiple errors detected - system health check recommended' : null,
    recentNotifications.length === 0 ? 'â„¹ï¸ No recent notifications - system running smoothly' : null,
    'ğŸ“Š Regular monitoring recommended'
  ].filter(Boolean)
};

return [{ json: { notificationAnalytics: analytics } }];
