// Enhanced Notification Formatter with Real Data Integration
const notification = $input.first().json.body;
const timestamp = new Date().toISOString();
const readableTime = new Date().toLocaleString('en-US', { 
  timeZone: 'America/New_York',
  year: 'numeric', 
  month: 'short', 
  day: 'numeric', 
  hour: '2-digit', 
  minute: '2-digit'
});

// Priority and urgency mapping
const priorityConfig = {
  CRITICAL: { emoji: 'üî•', color: '#8B0000', urgency: 'CRITICAL' },
  HIGH: { emoji: 'üö®', color: '#FF4444', urgency: 'URGENT' },
  MEDIUM: { emoji: '‚ö†Ô∏è', color: '#FFA500', urgency: 'MEDIUM' },
  LOW: { emoji: '‚ÑπÔ∏è', color: '#4CAF50', urgency: 'LOW' }
};

// Enhanced Type-specific formatting with real data
const typeFormatters = {
  TRADING_OPPORTUNITY: (data) => ({
    telegramMessage: `${priorityConfig.HIGH.emoji} <b>TRADING SIGNAL</b>\n\n` +
                    `üìä <b>Symbol:</b> ${data.symbol?.toUpperCase() || 'Unknown'}\n` +
                    `üìà <b>Action:</b> ${data.action || 'Unknown'}\n` +
                    `üéØ <b>Confidence:</b> ${data.confidence || 'N/A'}\n` +
                    `üí∞ <b>Price:</b> ${data.price || 'N/A'}\n` +
                    `üìä <b>Score:</b> ${data.score || 'N/A'}\n` +
                    `üß† <b>Reasoning:</b> ${data.reasoning || 'AI signal generated'}\n\n` +
                    `‚è∞ <i>${readableTime}</i>`,
    title: `Trading Opportunity: ${data.symbol?.toUpperCase() || 'Unknown'}`,
    description: `${data.action} signal with ${data.confidence} confidence`,
    color: priorityConfig.HIGH.color,
    priority: 'HIGH'
  }),

  RISK_ALERT: (data) => ({
    telegramMessage: `${priorityConfig.CRITICAL.emoji} <b>RISK ALERT</b>\n\n` +
                    `‚ö†Ô∏è <b>Level:</b> ${data.level || 'Unknown'}\n` +
                    `üìä <b>Risk:</b> ${data.risk || 'N/A'}\n` +
                    `üíπ <b>P&L:</b> ${data.pnl || 'N/A'}\n` +
                    `üìà <b>Positions:</b> ${data.positions || 'N/A'}\n` +
                    `üîÑ <b>Leverage:</b> ${data.leverage || 'N/A'}\n` +
                    `üö® <b>Alerts:</b> ${Array.isArray(data.alerts) ? data.alerts.join(', ') : (data.alerts || 'Multiple alerts')}\n` +
                    `üí° <b>Recommendations:</b> ${data.recommendations || 'Review positions immediately'}\n\n` +
                    `‚è∞ <i>${readableTime}</i>`,
    title: 'Portfolio Risk Alert',
    description: data.message || 'Risk threshold exceeded',
    color: priorityConfig.CRITICAL.color,
    priority: 'CRITICAL'
  }),

  PORTFOLIO_PERFORMANCE: (data) => ({
    telegramMessage: `${priorityConfig.MEDIUM.emoji} <b>PORTFOLIO REPORT</b>\n\n` +
                    `üíº <b>Grade:</b> ${data.summary?.grade || 'N/A'}\n` +
                    `üìä <b>Equity:</b> ${data.summary?.equity || data.equity || 'N/A'}\n` +
                    `üíπ <b>Daily P&L:</b> ${data.summary?.dailyPnL || data.dailyPnL || 'N/A'}\n` +
                    `üìà <b>Positions:</b> ${data.summary?.positions || data.positions || 'N/A'}\n` +
                    `üéØ <b>Fill Rate:</b> ${data.summary?.fillRate || data.fillRate || 'N/A'}\n` +
                    `‚ö†Ô∏è <b>Alerts:</b> ${Array.isArray(data.alerts) ? data.alerts.join(', ') : (data.alerts || 'None')}\n\n` +
                    `üí° <b>Recommendations:</b>\n${Array.isArray(data.recommendations) ? data.recommendations.map(r => `‚Ä¢ ${r}`).join('\n') : (data.recommendations || 'Continue monitoring')}\n\n` +
                    `‚è∞ <i>${readableTime}</i>`,
    title: `Portfolio Report - Grade ${data.summary?.grade || 'Unknown'}`,
    description: 'Daily performance summary',
    color: data.summary?.grade === 'A' ? '#4CAF50' : 
           data.summary?.grade === 'F' ? '#FF4444' : '#FFA500',
    priority: data.priority || 'MEDIUM'
  }),

  MARKET_INTELLIGENCE: (data) => ({
    telegramMessage: `${priorityConfig.LOW.emoji} <b>MARKET UPDATE</b>\n\n` +
                    `üìä <b>Analysis:</b> ${data.action || 'Market analyzed'}\n` +
                    `üìà <b>Bias:</b> ${data.bias || 'N/A'}\n` +
                    `üí™ <b>Strength:</b> ${data.strength || 'N/A'}\n` +
                    `üéØ <b>Confidence:</b> ${data.confidence || 'N/A'}\n` +
                    `üîç <b>Opportunities:</b> ${data.opportunities || 'N/A'}\n` +
                    `üì° <b>Signals:</b> ${data.signals || 'No new signals'}\n\n` +
                    `‚è∞ <i>${readableTime}</i>`,
    title: 'Market Intelligence Update',
    description: `Market analysis complete - ${data.action || 'Analyzed'}`,
    color: priorityConfig.LOW.color,
    priority: 'LOW'
  })
};

// Format notification based on type
const formatter = typeFormatters[notification.type];
const formattedNotification = formatter ? formatter(notification) : {
  telegramMessage: `${priorityConfig.LOW.emoji} <b>TRADING BOT ALERT</b>\n\n` +
                  `üìä <b>Type:</b> ${notification.type || 'Unknown'}\n` +
                  `üìù <b>Message:</b> ${notification.message || 'No message provided'}\n\n` +
                  `‚è∞ <i>${readableTime}</i>`,
  title: 'Trading Bot Notification',
  description: 'Unknown notification type',
  color: priorityConfig.LOW.color,
  priority: 'LOW'
};

// Add metadata and audit information
const finalNotification = {
  ...formattedNotification,
  timestamp: readableTime,
  originalType: notification.type,
  sessionId: notification.sessionId || `session-${Date.now()}`,

  // Telegram specific formatting
  telegram: {
    message: formattedNotification.telegramMessage,
    parseMode: 'HTML',
    disableWebPagePreview: true,
    chatId: process.env.TELEGRAM_CHAT_ID || '@ai_trading_alerts' // Default channel
  },

  // Audit trail
  audit: {
    notificationId: `notif-${Date.now()}`,
    receivedAt: timestamp,
    processedAt: new Date().toISOString(),
    source: 'n8n-workflow',
    priority: formattedNotification.priority,
    type: notification.type
  },

  // Original data for logging
  originalData: notification
};

console.log('üì± Notification Processed:', {
  type: notification.type,
  priority: formattedNotification.priority,
  notificationId: finalNotification.audit.notificationId,
  messageLength: formattedNotification.telegramMessage?.length || 0
});

return [{ json: { notification: finalNotification } }];

