// Enhanced Error Handler with Telegram Recovery
const error = $input.first().json || { message: 'Unknown error' };
const timestamp = new Date().toISOString();

// Enhanced error categorization
const errorCategories = {
  TELEGRAM_ERROR: 'Telegram API failure',
  API_ERROR: 'External API failure',
  WEBHOOK_ERROR: 'Webhook processing failure',
  DATA_ERROR: 'Data processing failure',
  SYSTEM_ERROR: 'System/Network failure',
  VALIDATION_ERROR: 'Data validation failure'
};

const errorType = error.message?.includes('telegram') || error.message?.includes('bot') ? 'TELEGRAM_ERROR' :
                 error.code?.includes('ECONNREFUSED') ? 'API_ERROR' :
                 error.message?.includes('webhook') ? 'WEBHOOK_ERROR' :
                 error.message?.includes('validation') ? 'VALIDATION_ERROR' :
                 error.code?.includes('MODULE_NOT_FOUND') ? 'SYSTEM_ERROR' :
                 'SYSTEM_ERROR';

// Recovery strategies with Telegram fallback
const recoveryStrategies = {
  TELEGRAM_ERROR: {
    action: 'RETRY_TELEGRAM_WITH_FALLBACK',
    retryCount: 3,
    retryDelay: 10000,
    fallbackAction: 'LOG_TO_DATABASE_ONLY',
    escalate: false
  },
  API_ERROR: {
    action: 'RETRY_WITH_FALLBACK',
    retryCount: 3,
    retryDelay: 30000,
    fallbackAction: 'USE_CACHED_DATA',
    escalate: false
  },
  WEBHOOK_ERROR: {
    action: 'VALIDATE_AND_RETRY',
    retryCount: 2,
    retryDelay: 5000,
    fallbackAction: 'LOG_ERROR_ONLY',
    escalate: true
  },
  SYSTEM_ERROR: {
    action: 'SYSTEM_RESTART',
    retryCount: 1,
    escalate: true,
    fallbackAction: 'MAINTENANCE_MODE'
  },
  DATA_ERROR: {
    action: 'VALIDATE_AND_CLEAN',
    retryCount: 1,
    fallbackAction: 'USE_DEFAULT_VALUES',
    escalate: false
  }
};

const strategy = recoveryStrategies[errorType] || recoveryStrategies.SYSTEM_ERROR;

// Generate comprehensive error report
const errorReport = {
  errorId: `error-${Date.now()}`,
  timestamp,
  errorType,
  category: errorCategories[errorType],
  originalError: {
    message: error.message || 'Unknown error',
    code: error.code || 'NO_CODE',
    stack: error.stack || 'No stack trace',
    context: error.context || 'Notification processing',
    httpStatus: error.httpStatus || 'Unknown'
  },
  severity: strategy.escalate ? 'CRITICAL' : 'MEDIUM',
  recovery: {
    strategy: strategy.action,
    retryCount: strategy.retryCount,
    retryDelay: strategy.retryDelay,
    fallbackAction: strategy.fallbackAction,
    requiresEscalation: strategy.escalate || false
  },
  impact: {
    notificationAffected: true,
    telegramAffected: errorType === 'TELEGRAM_ERROR',
    dataAffected: errorType === 'DATA_ERROR',
    systemAffected: errorType === 'SYSTEM_ERROR',
    userNotificationRequired: strategy.escalate
  },
  recommendations: [
    strategy.escalate ? 'Immediate attention required' : null,
    errorType === 'TELEGRAM_ERROR' ? 'Check Telegram bot token and permissions' : null,
    errorType === 'API_ERROR' ? 'Check API endpoints and credentials' : null,
    errorType === 'WEBHOOK_ERROR' ? 'Validate webhook payload structure' : null,
    'Monitor system stability after recovery',
    'Review notification delivery logs'
  ].filter(Boolean),

  // Telegram error message for fallback
  telegramErrorMessage: `üö´ <b>SYSTEM ERROR</b>\n\n` +
                       `‚ö†Ô∏è <b>Type:</b> ${errorType}\n` +
                       `üìù <b>Message:</b> ${error.message?.substring(0, 100) || 'Unknown error'}\n` +
                       `üîß <b>Recovery:</b> ${strategy.action}\n\n` +
                       `‚è∞ <i>${new Date().toLocaleString('en-US', { timeZone: 'America/New_York' })}</i>`
};

console.log('üö´ Error Report Generated:', {
  errorType,
  severity: errorReport.severity,
  requiresEscalation: errorReport.recovery.requiresEscalation,
  errorId: errorReport.errorId
});

return [{ json: { errorReport, originalError: error } }];

// Comprehensive Notification Analytics
const notifications = $input.all();
const timestamp = new Date().toISOString();
const timeWindow = 3600000; // 1 hour
const now = Date.now();

// Filter recent notifications
const recentNotifications = notifications.filter(n => {
  const notifTime = new Date(n.json.notification?.timestamp || n.json.processedAt || timestamp).getTime();
  return (now - notifTime) <= timeWindow;
});

// Enhanced analytics with Telegram delivery tracking
const analytics = {
  summary: {
    totalNotifications: notifications.length,
    recentNotifications: recentNotifications.length,
    timeWindow: '1 hour',
    analysisTime: timestamp,
    telegramDeliveryRate: notifications.filter(n => n.json.telegramStatus === 'SUCCESS').length / Math.max(notifications.length, 1) * 100
  },

  byType: recentNotifications.reduce((acc, n) => {
    const type = n.json.notification?.originalType || n.json.originalType || 'UNKNOWN';
    acc[type] = (acc[type] || 0) + 1;
    return acc;
  }, {}),

  byPriority: recentNotifications.reduce((acc, n) => {
    const priority = n.json.notification?.priority || n.json.priority || 'LOW';
    acc[priority] = (acc[priority] || 0) + 1;
    return acc;
  }, {}),

  deliveryMetrics: {
    totalSent: notifications.length,
    successfulDeliveries: notifications.filter(n => n.json.telegramStatus === 'SUCCESS').length,
    failedDeliveries: notifications.filter(n => n.json.telegramStatus === 'FAILED').length,
    deliveryRate: notifications.length > 0 ? 
      (notifications.filter(n => n.json.telegramStatus === 'SUCCESS').length / notifications.length * 100).toFixed(1) + '%' : '0%'
  },

  alerts: {
    highVolumeAlert: recentNotifications.length > 15,
    criticalAlerts: recentNotifications.filter(n => 
      (n.json.notification?.priority || n.json.priority) === 'CRITICAL').length,
    deliveryIssues: notifications.filter(n => n.json.telegramStatus === 'FAILED').length > 2,
    errorAlerts: recentNotifications.filter(n => n.json.errorReport).length,
    tradingAlerts: recentNotifications.filter(n => 
      (n.json.notification?.originalType || n.json.originalType) === 'TRADING_OPPORTUNITY').length
  },

  trends: {
    averageNotificationsPerHour: recentNotifications.length,
    mostCommonType: Object.entries(recentNotifications.reduce((acc, n) => {
      const type = n.json.notification?.originalType || n.json.originalType || 'UNKNOWN';
      acc[type] = (acc[type] || 0) + 1;
      return acc;
    }, {})).sort(([,a], [,b]) => b - a)[0]?.[0] || 'NONE',
    criticalRate: recentNotifications.length > 0 ? 
      (recentNotifications.filter(n => 
        (n.json.notification?.priority || n.json.priority) === 'CRITICAL').length / recentNotifications.length * 100).toFixed(1) + '%' : '0%',
    hourlyDistribution: 'Implementation for hourly patterns'
  },

  recommendations: [
    recentNotifications.length > 20 ? '‚ö†Ô∏è Very high notification volume - check for notification spam' : null,
    recentNotifications.filter(n => 
      (n.json.notification?.priority || n.json.priority) === 'CRITICAL').length > 5 ? 
      'üö® Multiple critical alerts - immediate system review required' : null,
    notifications.filter(n => n.json.telegramStatus === 'FAILED').length > 3 ? 
      'üì± Telegram delivery issues - check bot token and permissions' : null,
    recentNotifications.filter(n => n.json.errorReport).length > 2 ? 
      'üîß Multiple system errors - health check recommended' : null,
    recentNotifications.length === 0 ? '‚ÑπÔ∏è No recent notifications - system quiet' : null,
    'üìä Continue monitoring notification patterns and delivery rates'
  ].filter(Boolean),

  performance: {
    avgProcessingTime: 'Implementation for processing time tracking',
    peakHours: 'Implementation for peak usage analysis',
    reliabilityScore: Math.max(0, 100 - (notifications.filter(n => n.json.telegramStatus === 'FAILED').length * 10))
  }
};

return [{ json: { notificationAnalytics: analytics } }];
