// Enhanced Portfolio Analytics with Real Alpaca Data
const account = $input.first().json || {};
const positions = $input.all()[1].json || [];
const orders = $input.all()[2].json || [];
const timestamp = new Date().toISOString();

console.log('üíº Processing Portfolio Data:', {
  accountStatus: account.status,
  positionCount: Array.isArray(positions) ? positions.length : 0,
  orderCount: Array.isArray(orders) ? orders.length : 0,
  timestamp
});

// Account Overview from Alpaca
const accountSummary = {
  accountId: account.id || 'unknown',
  status: account.status || 'unknown',
  currency: account.currency || 'USD',
  buyingPower: parseFloat(account.buying_power || 0),
  cash: parseFloat(account.cash || 0),
  portfolioValue: parseFloat(account.portfolio_value || 0),
  equity: parseFloat(account.equity || 0),
  lastEquity: parseFloat(account.last_equity || 0),
  daytradeCount: parseInt(account.daytrade_count || 0),
  multiplier: parseFloat(account.multiplier || 1)
};

// Calculate daily P&L
const dailyPnL = accountSummary.equity - accountSummary.lastEquity;
const dailyPnLPercentage = accountSummary.lastEquity > 0 ? (dailyPnL / accountSummary.lastEquity) * 100 : 0;

// Process positions
const positionDetails = Array.isArray(positions) ? positions.map(pos => {
  const marketValue = parseFloat(pos.market_value || 0);
  const unrealizedPnL = parseFloat(pos.unrealized_pl || 0);
  const unrealizedPnLPercent = parseFloat(pos.unrealized_plpc || 0) * 100;
  const costBasis = parseFloat(pos.cost_basis || 0);

  return {
    symbol: pos.symbol,
    qty: parseFloat(pos.qty || 0),
    side: pos.side,
    marketValue,
    avgEntryPrice: parseFloat(pos.avg_entry_price || 0),
    currentPrice: parseFloat(pos.current_price || 0),
    costBasis,
    unrealizedPnL,
    unrealizedPnLPercent: parseFloat(unrealizedPnLPercent.toFixed(2)),
    changeTodayPercent: parseFloat((pos.change_today || 0) * 100).toFixed(2)
  };
}) : [];

// Calculate portfolio metrics
const totalPositionValue = positionDetails.reduce((sum, pos) => sum + pos.marketValue, 0);
const totalUnrealizedPnL = positionDetails.reduce((sum, pos) => sum + pos.unrealizedPnL, 0);
const numberOfPositions = positionDetails.length;
const largestPosition = Math.max(...positionDetails.map(p => Math.abs(p.marketValue)), 0);
const positionConcentration = totalPositionValue > 0 ? (largestPosition / totalPositionValue) * 100 : 0;

// Process recent orders for performance analysis
const recentOrders = Array.isArray(orders) ? orders.slice(0, 20) : [];
const filledOrders = recentOrders.filter(order => order.status === 'filled');
const cancelledOrders = recentOrders.filter(order => order.status === 'cancelled');
const orderFillRate = recentOrders.length > 0 ? (filledOrders.length / recentOrders.length) * 100 : 0;

// Risk Assessment
const riskMetrics = {
  portfolioValue: accountSummary.portfolioValue,
  cashPosition: accountSummary.cash,
  buyingPowerUsed: accountSummary.portfolioValue - accountSummary.cash,
  buyingPowerUtilization: accountSummary.buyingPower > 0 ? 
    ((accountSummary.portfolioValue - accountSummary.cash) / accountSummary.buyingPower) * 100 : 0,
  positionCount: numberOfPositions,
  positionConcentration: parseFloat(positionConcentration.toFixed(2)),
  largestPositionValue: largestPosition,
  daytradingCount: accountSummary.daytradeCount,
  leverage: accountSummary.multiplier
};

// Performance Metrics
const performanceMetrics = {
  dailyPnL: parseFloat(dailyPnL.toFixed(2)),
  dailyPnLPercentage: parseFloat(dailyPnLPercentage.toFixed(2)),
  totalUnrealizedPnL: parseFloat(totalUnrealizedPnL.toFixed(2)),
  totalUnrealizedPnLPercent: accountSummary.portfolioValue > 0 ? 
    parseFloat(((totalUnrealizedPnL / accountSummary.portfolioValue) * 100).toFixed(2)) : 0,
  orderFillRate: parseFloat(orderFillRate.toFixed(1)),
  totalOrders: recentOrders.length,
  filledOrders: filledOrders.length,
  cancelledOrders: cancelledOrders.length
};

// Generate Alerts
const alerts = {
  highRisk: riskMetrics.buyingPowerUtilization > 80,
  lowCash: riskMetrics.cashPosition < (accountSummary.portfolioValue * 0.1), // Less than 10% cash
  concentrationRisk: riskMetrics.positionConcentration > 50,
  largeDrawdown: performanceMetrics.dailyPnLPercentage < -5,
  highDaytrading: riskMetrics.daytradingCount > 2, // Approaching PDT limit
  unusualActivity: performanceMetrics.totalOrders > 20,
  lowFillRate: performanceMetrics.orderFillRate < 80
};

// Recommendations
const recommendations = {
  action: performanceMetrics.dailyPnLPercentage > 2 ? 'MONITOR_PROFITS' : 
         performanceMetrics.dailyPnLPercentage < -3 ? 'REVIEW_POSITIONS' : 'CONTINUE_MONITORING',
  priority: Object.values(alerts).some(Boolean) ? 'HIGH' : 
           Math.abs(performanceMetrics.dailyPnLPercentage) > 1 ? 'MEDIUM' : 'LOW',
  suggestions: [
    alerts.highRisk ? 'Reduce position sizes or increase cash reserves' : null,
    alerts.concentrationRisk ? 'Diversify portfolio allocation' : null,
    alerts.lowCash ? 'Consider reducing positions to maintain cash buffer' : null,
    alerts.largeDrawdown ? 'Review stop-loss strategies and risk management' : null,
    alerts.highDaytrading ? 'Monitor day trading count to avoid PDT restrictions' : null,
    alerts.lowFillRate ? 'Review order types and market conditions' : null,
    performanceMetrics.dailyPnLPercentage > 3 ? 'Consider taking profits on winning positions' : null
  ].filter(Boolean)
};

// Comprehensive Portfolio Report
const portfolioAnalysis = {
  timestamp,
  account: accountSummary,
  positions: positionDetails,
  riskMetrics,
  performanceMetrics,
  alerts,
  recommendations,
  marketData: {
    tradingDay: new Date().toDateString(),
    marketHours: 'Check if market is open', // Could be enhanced with market hours API
    lastUpdate: timestamp
  }
};

return [{ json: { portfolioAnalysis } }];

