// Generate Enhanced Daily Portfolio Report
const analysis = $input.first().json.portfolioAnalysis;
const timestamp = new Date().toISOString();
const date = new Date().toDateString();

// Enhanced Performance Summary
const summary = {
  reportDate: date,
  reportTime: timestamp,
  accountOverview: {
    accountId: analysis.account.accountId,
    portfolioValue: `$${analysis.account.portfolioValue.toLocaleString()}`,
    equity: `$${analysis.account.equity.toLocaleString()}`,
    cash: `$${analysis.account.cash.toLocaleString()}`,
    buyingPower: `$${analysis.account.buyingPower.toLocaleString()}`,
    dailyPnL: `${analysis.performanceMetrics.dailyPnL >= 0 ? '+' : ''}$${analysis.performanceMetrics.dailyPnL.toLocaleString()}`,
    dailyPnLPercent: `${analysis.performanceMetrics.dailyPnLPercentage >= 0 ? '+' : ''}${analysis.performanceMetrics.dailyPnLPercentage}%`
  },
  tradingActivity: {
    activePositions: analysis.riskMetrics.positionCount,
    totalOrders: analysis.performanceMetrics.totalOrders,
    filledOrders: analysis.performanceMetrics.filledOrders,
    orderFillRate: `${analysis.performanceMetrics.orderFillRate}%`,
    daytradeCount: analysis.riskMetrics.daytradingCount,
    buyingPowerUtilization: `${analysis.riskMetrics.buyingPowerUtilization.toFixed(1)}%`
  },
  riskAssessment: {
    riskLevel: analysis.recommendations.priority,
    positionConcentration: `${analysis.riskMetrics.positionConcentration}%`,
    largestPosition: `$${analysis.riskMetrics.largestPositionValue.toLocaleString()}`,
    leverage: `${analysis.riskMetrics.leverage}x`,
    activeAlerts: Object.keys(analysis.alerts).filter(key => analysis.alerts[key]).length
  },
  topPositions: analysis.positions.slice(0, 5).map(pos => ({
    symbol: pos.symbol,
    side: pos.side,
    qty: pos.qty,
    marketValue: `$${pos.marketValue.toLocaleString()}`,
    unrealizedPnL: `${pos.unrealizedPnL >= 0 ? '+' : ''}$${pos.unrealizedPnL.toFixed(2)}`,
    unrealizedPnLPercent: `${pos.unrealizedPnLPercent >= 0 ? '+' : ''}${pos.unrealizedPnLPercent}%`,
    currentPrice: `$${pos.currentPrice.toFixed(2)}`
  })),
  alerts: Object.keys(analysis.alerts).filter(key => analysis.alerts[key]),
  recommendations: analysis.recommendations.suggestions,
  marketInsights: {
    recommendedAction: analysis.recommendations.action,
    priority: analysis.recommendations.priority,
    nextReview: new Date(Date.now() + 2 * 60 * 60 * 1000).toISOString() // 2 hours
  }
};

// Generate Performance Grade
const gradeFactors = [
  analysis.performanceMetrics.dailyPnLPercentage >= 0 ? 25 : Math.max(0, 25 + analysis.performanceMetrics.dailyPnLPercentage * 5),
  analysis.performanceMetrics.orderFillRate >= 90 ? 25 : analysis.performanceMetrics.orderFillRate * 0.25,
  analysis.riskMetrics.buyingPowerUtilization <= 60 ? 25 : Math.max(0, 25 - (analysis.riskMetrics.buyingPowerUtilization - 60) * 0.5),
  Object.values(analysis.alerts).filter(Boolean).length === 0 ? 25 : Math.max(0, 25 - Object.values(analysis.alerts).filter(Boolean).length * 5)
];

const performanceScore = gradeFactors.reduce((sum, factor) => sum + factor, 0);
const performanceGrade = performanceScore >= 85 ? 'A' : 
                       performanceScore >= 70 ? 'B' : 
                       performanceScore >= 55 ? 'C' : 
                       performanceScore >= 40 ? 'D' : 'F';

const dailyReport = {
  ...summary,
  performanceGrade: {
    score: Math.round(performanceScore),
    grade: performanceGrade,
    breakdown: {
      dailyReturns: Math.round(gradeFactors[0]),
      orderExecution: Math.round(gradeFactors[1]),
      riskManagement: Math.round(gradeFactors[2]),
      alertStatus: Math.round(gradeFactors[3])
    }
  }
};

return [{ json: { dailyReport, rawAnalysis: analysis } }];

