// Enhanced Authentication & Request Validation
const headers = $json.headers || {};
const body = $json.body || {};
const query = $json.query || {};
const method = headers['x-http-method'] || 'POST';
const timestamp = new Date().toISOString();

// Extract authentication
const authHeader = headers.Authorization || headers.authorization || headers['X-API-Key'] || '';
const expectedBearer = `Bearer ${process.env.API_INTEGRATION_BEARER_TOKEN || 'ai-trading-bot-secure-2025-integration'}`;

console.log('ğŸ” API Gateway Request:', {
  hasAuth: !!authHeader,
  method,
  bodyType: typeof body,
  timestamp,
  userAgent: headers['user-agent'] || headers['User-Agent'] || 'Unknown',
  origin: headers.origin || headers.Origin || 'Unknown'
});

// Comprehensive validation
const validationResult = {
  authValid: authHeader === expectedBearer,
  hasBody: typeof body === 'object' && body !== null,
  hasAction: body.action || body.type || body.workflow,
  validMethod: ['POST', 'GET'].includes(method.toUpperCase())
};

// Security checks
const securityChecks = {
  suspiciousUserAgent: (headers['user-agent'] || '').includes('bot') && !(headers['user-agent'] || '').includes('TradingBot'),
  rateLimitExceeded: false, // Implement rate limiting logic
  ipWhitelisted: true, // Implement IP whitelisting if needed
  validTimestamp: Math.abs(Date.now() - (body.timestamp ? new Date(body.timestamp).getTime() : Date.now())) < 300000 // 5 min window
};

// Create request context
const requestContext = {
  requestId: `req-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  timestamp,
  clientInfo: {
    userAgent: headers['user-agent'] || headers['User-Agent'] || 'Unknown',
    origin: headers.origin || headers.Origin || 'Unknown',
    ip: headers['x-forwarded-for'] || headers['X-Forwarded-For'] || 'Unknown'
  },
  authentication: {
    method: authHeader.startsWith('Bearer') ? 'Bearer Token' : 'Unknown',
    valid: validationResult.authValid
  },
  validation: validationResult,
  security: securityChecks
};

// Early validation failures
if (!validationResult.authValid) {
  return [{ 
    json: { 
      success: false, 
      error: 'Unauthorized - Invalid or missing authentication', 
      statusCode: 401,
      requestId: requestContext.requestId,
      timestamp
    } 
  }];
}

if (!validationResult.hasBody) {
  return [{ 
    json: { 
      success: false, 
      error: 'Bad Request - Invalid or missing JSON body', 
      statusCode: 400,
      requestId: requestContext.requestId,
      timestamp
    } 
  }];
}

if (!validationResult.hasAction) {
  return [{ 
    json: { 
      success: false, 
      error: 'Bad Request - Missing action, type, or workflow parameter', 
      statusCode: 400,
      requestId: requestContext.requestId,
      timestamp
    } 
  }];
}

// Security warning checks
if (Object.values(securityChecks).some(check => check === false)) {
  console.log('âš ï¸ Security Warning:', {
    requestId: requestContext.requestId,
    failedChecks: Object.keys(securityChecks).filter(key => securityChecks[key] === false),
    clientInfo: requestContext.clientInfo
  });
}

// Determine routing based on request
const routingLogic = {
  action: body.action || body.type || body.workflow,
  targetWorkflow: null,
  parameters: {},
  priority: 'MEDIUM'
};

// Enhanced routing decision
switch (routingLogic.action.toUpperCase()) {
  case 'TRADING':
  case 'TRADE':
  case 'AI_TRADING':
  case 'MARKET_ANALYSIS':
    routingLogic.targetWorkflow = 'enhanced-ai-trading-master';
    routingLogic.priority = 'HIGH';
    routingLogic.parameters = {
      symbol: body.symbol || 'BTC',
      capital: body.capital || process.env.TRADING_CAPITAL || '50000',
      riskTolerance: body.riskTolerance || process.env.RISK_TOLERANCE || 'MEDIUM'
    };
    break;

  case 'PORTFOLIO':
  case 'PERFORMANCE':
  case 'RISK':
  case 'MONITORING':
    routingLogic.targetWorkflow = 'enhanced-portfolio-monitor';
    routingLogic.priority = 'MEDIUM';
    routingLogic.parameters = {
      accountId: body.accountId || 'default',
      includeHistory: body.includeHistory !== false
    };
    break;

  case 'NOTIFICATION':
  case 'ALERT':
  case 'NOTIFY':
    routingLogic.targetWorkflow = 'enhanced-notification-system';
    routingLogic.priority = body.priority || 'MEDIUM';
    routingLogic.parameters = {
      type: body.notificationType || 'MANUAL',
      message: body.message || 'Manual notification',
      urgent: body.urgent === true
    };
    break;

  default:
    return [{ 
      json: { 
        success: false, 
        error: `Unsupported action: ${routingLogic.action}`, 
        statusCode: 400,
        supportedActions: ['TRADING', 'PORTFOLIO', 'NOTIFICATION'],
        requestId: requestContext.requestId,
        timestamp
      } 
    }];
}

// Prepare final request payload
const processedRequest = {
  success: true,
  routing: routingLogic,
  context: requestContext,
  payload: {
    ...body,
    ...routingLogic.parameters,
    requestId: requestContext.requestId,
    timestamp,
    priority: routingLogic.priority
  },
  metadata: {
    processedAt: timestamp,
    version: '2.0.0',
    source: 'n8n-api-gateway'
  }
};

console.log('âœ… Request Processed Successfully:', {
  requestId: requestContext.requestId,
  targetWorkflow: routingLogic.targetWorkflow,
  action: routingLogic.action,
  priority: routingLogic.priority
});

return [{ json: processedRequest }];

// Enhanced Response Aggregator with Comprehensive Status
const responses = $input.all();
const timestamp = new Date().toISOString();

// Find the main request data (should be first in the chain)
const mainRequest = responses.find(r => r.json.context?.requestId) || responses[0];
const requestId = mainRequest?.json?.context?.requestId || `fallback-${Date.now()}`;

// Aggregate workflow execution results
const workflowResults = responses.filter(r => 
  r.json && (r.json.success !== undefined || r.json.executed !== undefined || r.json.status)
);

// Determine overall execution status
const executionStatus = {
  totalRequests: workflowResults.length,
  successfulExecutions: workflowResults.filter(r => 
    r.json.success === true || r.json.executed === true || r.json.status === 'success'
  ).length,
  failedExecutions: workflowResults.filter(r => 
    r.json.success === false || r.json.error || r.json.status === 'error'
  ).length,
  pendingExecutions: workflowResults.filter(r => 
    r.json.status === 'pending' || r.json.status === 'processing'
  ).length
};

// Calculate execution metrics
const executionMetrics = {
  successRate: executionStatus.totalRequests > 0 ? 
    (executionStatus.successfulExecutions / executionStatus.totalRequests * 100).toFixed(1) + '%' : '0%',
  executionTime: mainRequest?.json?.context?.timestamp ? 
    Date.now() - new Date(mainRequest.json.context.timestamp).getTime() : 0,
  workflowsTriggered: executionStatus.totalRequests,
  overallStatus: executionStatus.failedExecutions > 0 ? 'PARTIAL_SUCCESS' :
                 executionStatus.successfulExecutions > 0 ? 'SUCCESS' : 'PENDING'
};

// Extract detailed results
const detailedResults = workflowResults.map(result => ({
  workflow: result.json.workflow || 'unknown',
  status: result.json.success === true ? 'SUCCESS' : 
          result.json.success === false ? 'FAILED' : 'UNKNOWN',
  data: result.json.data || result.json.result || null,
  error: result.json.error || null,
  executionId: result.json.executionId || null,
  timestamp: result.json.timestamp || timestamp
}));

// Create comprehensive response
const aggregatedResponse = {
  success: executionStatus.failedExecutions === 0 && executionStatus.successfulExecutions > 0,
  requestId,
  timestamp,
  execution: {
    status: executionMetrics.overallStatus,
    metrics: executionMetrics,
    summary: executionStatus,
    details: detailedResults
  },
  routing: mainRequest?.json?.routing || {
    action: 'unknown',
    targetWorkflow: 'unknown',
    priority: 'MEDIUM'
  },
  data: {
    results: detailedResults,
    aggregatedData: workflowResults.reduce((acc, result) => {
      if (result.json.data) {
        acc[result.json.workflow || 'unknown'] = result.json.data;
      }
      return acc;
    }, {})
  },
  metadata: {
    version: '2.0.0',
    processedAt: timestamp,
    totalExecutionTime: executionMetrics.executionTime + 'ms',
    apiGateway: 'n8n-enhanced-integration'
  }
};

// Add errors if any
const errors = workflowResults
  .filter(r => r.json.error)
  .map(r => ({
    workflow: r.json.workflow || 'unknown',
    error: r.json.error,
    details: r.json.details || null
  }));

if (errors.length > 0) {
  aggregatedResponse.errors = errors;
}

// Add warnings for partial success
if (executionMetrics.overallStatus === 'PARTIAL_SUCCESS') {
  aggregatedResponse.warnings = [
    `${executionStatus.failedExecutions} out of ${executionStatus.totalRequests} workflows failed`,
    'Some functionality may not be available',
    'Check error details for specific workflow failures'
  ];
}

console.log('ğŸ”— API Integration Response:', {
  requestId,
  overallStatus: executionMetrics.overallStatus,
  successRate: executionMetrics.successRate,
  executionTime: executionMetrics.executionTime + 'ms',
  workflowsTriggered: executionStatus.totalRequests
});

return [{ json: aggregatedResponse }];
