// Advanced Error Analysis and Classification
const errorData = $json.body || $json;
const timestamp = new Date().toISOString();

console.log('ğŸš¨ Error Handler Processing:', {
  errorType: errorData.type || 'unknown',
  source: errorData.source || 'unknown',
  timestamp
});

// Enhanced error categorization
const errorCategories = {
  TRADING_ERROR: {
    priority: 'CRITICAL',
    description: 'Trading execution or order management failure',
    recoveryStrategy: 'EMERGENCY_STOP',
    escalate: true,
    maxRetries: 0
  },
  API_ERROR: {
    priority: 'HIGH',
    description: 'External API failure (Alpaca, Binance, etc.)',
    recoveryStrategy: 'RETRY_WITH_FALLBACK',
    escalate: false,
    maxRetries: 3,
    retryDelay: 30000
  },
  DATABASE_ERROR: {
    priority: 'HIGH',
    description: 'Database connection or query failure',
    recoveryStrategy: 'RETRY_WITH_CACHE',
    escalate: true,
    maxRetries: 2,
    retryDelay: 10000
  },
  NOTIFICATION_ERROR: {
    priority: 'MEDIUM',
    description: 'Notification delivery failure',
    recoveryStrategy: 'RETRY_ALTERNATIVE_CHANNEL',
    escalate: false,
    maxRetries: 3,
    retryDelay: 5000
  },
  SECURITY_ERROR: {
    priority: 'CRITICAL',
    description: 'Security violation or authentication failure',
    recoveryStrategy: 'IMMEDIATE_LOCKDOWN',
    escalate: true,
    maxRetries: 0
  },
  SYSTEM_ERROR: {
    priority: 'HIGH',
    description: 'System-level error or resource failure',
    recoveryStrategy: 'SYSTEM_RESTART',
    escalate: true,
    maxRetries: 1,
    retryDelay: 60000
  },
  VALIDATION_ERROR: {
    priority: 'MEDIUM',
    description: 'Data validation or format error',
    recoveryStrategy: 'CLEAN_AND_RETRY',
    escalate: false,
    maxRetries: 2,
    retryDelay: 1000
  },
  NETWORK_ERROR: {
    priority: 'MEDIUM',
    description: 'Network connectivity or timeout error',
    recoveryStrategy: 'EXPONENTIAL_BACKOFF',
    escalate: false,
    maxRetries: 5,
    retryDelay: 5000
  }
};

// Determine error type from various sources
function determineErrorType(error) {
  const errorMessage = (error.message || error.error || '').toLowerCase();
  const errorCode = error.code || error.statusCode || '';
  const errorSource = (error.source || '').toLowerCase();

  // Security-related errors
  if (errorMessage.includes('unauthorized') || errorMessage.includes('forbidden') || 
      errorMessage.includes('authentication') || errorCode === 401 || errorCode === 403) {
    return 'SECURITY_ERROR';
  }

  // Trading-related errors
  if (errorSource.includes('trading') || errorSource.includes('order') || 
      errorSource.includes('execution') || errorMessage.includes('trade') ||
      errorMessage.includes('position') || errorMessage.includes('balance')) {
    return 'TRADING_ERROR';
  }

  // API-related errors
  if (errorMessage.includes('alpaca') || errorMessage.includes('binance') || 
      errorMessage.includes('coingecko') || errorMessage.includes('api') ||
      errorCode >= 400 && errorCode < 500) {
    return 'API_ERROR';
  }

  // Database-related errors
  if (errorMessage.includes('database') || errorMessage.includes('supabase') ||
      errorMessage.includes('sql') || errorMessage.includes('connection')) {
    return 'DATABASE_ERROR';
  }

  // Notification errors
  if (errorSource.includes('notification') || errorSource.includes('telegram') ||
      errorMessage.includes('telegram') || errorMessage.includes('notification')) {
    return 'NOTIFICATION_ERROR';
  }

  // Network errors
  if (errorMessage.includes('network') || errorMessage.includes('timeout') ||
      errorMessage.includes('econnrefused') || errorMessage.includes('enotfound') ||
      errorCode >= 500) {
    return 'NETWORK_ERROR';
  }

  // Validation errors
  if (errorMessage.includes('validation') || errorMessage.includes('invalid') ||
      errorMessage.includes('missing') || errorCode === 400) {
    return 'VALIDATION_ERROR';
  }

  // Default to system error
  return 'SYSTEM_ERROR';
}

const errorType = determineErrorType(errorData);
const errorConfig = errorCategories[errorType];

// Generate comprehensive error report
const errorReport = {
  errorId: `error-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  timestamp,
  classification: {
    type: errorType,
    priority: errorConfig.priority,
    description: errorConfig.description,
    escalationRequired: errorConfig.escalate
  },
  originalError: {
    message: errorData.message || errorData.error || 'Unknown error',
    code: errorData.code || errorData.statusCode || null,
    source: errorData.source || 'unknown',
    stack: errorData.stack || null,
    context: errorData.context || {},
    requestId: errorData.requestId || null,
    workflowId: errorData.workflowId || null
  },
  recovery: {
    strategy: errorConfig.recoveryStrategy,
    maxRetries: errorConfig.maxRetries,
    retryDelay: errorConfig.retryDelay || 0,
    currentRetryCount: errorData.retryCount || 0,
    canRetry: (errorData.retryCount || 0) < errorConfig.maxRetries,
    estimatedRecoveryTime: errorConfig.retryDelay ? 
      new Date(Date.now() + errorConfig.retryDelay).toISOString() : null
  },
  impact: {
    tradingAffected: errorType === 'TRADING_ERROR' || errorType === 'API_ERROR',
    dataIntegrityAffected: errorType === 'DATABASE_ERROR',
    securityCompromised: errorType === 'SECURITY_ERROR',
    notificationAffected: errorType === 'NOTIFICATION_ERROR',
    systemStabilityAffected: errorType === 'SYSTEM_ERROR',
    userExperienceAffected: true
  },
  recommendations: [
    errorConfig.escalate ? 'Immediate attention required' : null,
    errorType === 'TRADING_ERROR' ? 'Stop all trading operations immediately' : null,
    errorType === 'SECURITY_ERROR' ? 'Review security logs and credentials' : null,
    errorType === 'API_ERROR' ? 'Check API credentials and rate limits' : null,
    errorType === 'DATABASE_ERROR' ? 'Verify database connectivity and queries' : null,
    errorType === 'NETWORK_ERROR' ? 'Check network connectivity and DNS resolution' : null,
    'Monitor system stability after recovery',
    'Review error patterns for potential system improvements'
  ].filter(Boolean),
  metrics: {
    detectionTime: timestamp,
    processingTime: Date.now(),
    affectedSystems: [errorData.source].filter(Boolean),
    errorFrequency: 1, // This would be enhanced with historical data
    criticalityScore: errorConfig.priority === 'CRITICAL' ? 100 : 
                      errorConfig.priority === 'HIGH' ? 75 : 
                      errorConfig.priority === 'MEDIUM' ? 50 : 25
  }
};

// Generate recovery actions based on error type
const recoveryActions = {
  'EMERGENCY_STOP': {
    immediate: ['Close all positions', 'Stop trading engine', 'Send critical alert'],
    followUp: ['Review trade logs', 'Analyze root cause', 'Manual intervention required']
  },
  'RETRY_WITH_FALLBACK': {
    immediate: ['Retry with exponential backoff', 'Switch to backup API if available'],
    followUp: ['Monitor API health', 'Update API credentials if needed']
  },
  'IMMEDIATE_LOCKDOWN': {
    immediate: ['Revoke API access', 'Stop all operations', 'Alert security team'],
    followUp: ['Security audit', 'Credential rotation', 'System hardening']
  },
  'SYSTEM_RESTART': {
    immediate: ['Graceful service restart', 'Health check all components'],
    followUp: ['Monitor system resources', 'Review system logs']
  },
  'RETRY_ALTERNATIVE_CHANNEL': {
    immediate: ['Try backup notification channel', 'Log delivery failure'],
    followUp: ['Review notification configuration', 'Test all channels']
  }
};

const actionPlan = recoveryActions[errorConfig.recoveryStrategy] || {
  immediate: ['Log error', 'Monitor for recurrence'],
  followUp: ['Manual investigation required']
};

// Final error analysis
const finalReport = {
  ...errorReport,
  recoveryPlan: {
    strategy: errorConfig.recoveryStrategy,
    immediateActions: actionPlan.immediate,
    followUpActions: actionPlan.followUp,
    automatedRecovery: errorConfig.maxRetries > 0,
    manualInterventionRequired: errorConfig.escalate
  },
  notificationRequired: {
    critical: errorConfig.priority === 'CRITICAL',
    telegram: errorConfig.escalate || errorConfig.priority === 'CRITICAL',
    email: errorConfig.escalate,
    dashboard: true,
    logs: true
  }
};

console.log('ğŸ“Š Error Analysis Complete:', {
  errorId: finalReport.errorId,
  type: errorType,
  priority: errorConfig.priority,
  canRetry: finalReport.recovery.canRetry,
  escalationRequired: errorConfig.escalate
});

return [{ json: { errorAnalysis: finalReport } }];

// Critical Error Response - Immediate Actions
const errorAnalysis = $json.errorAnalysis;
const timestamp = new Date().toISOString();

console.log('ğŸš¨ CRITICAL ERROR RESPONSE ACTIVATED:', {
  errorId: errorAnalysis.errorId,
  type: errorAnalysis.classification.type,
  source: errorAnalysis.originalError.source
});

// Execute immediate critical actions
const criticalActions = {
  'TRADING_ERROR': async () => {
    // Emergency trading stop
    return {
      action: 'EMERGENCY_TRADING_STOP',
      executed: true,
      details: 'All trading operations halted immediately'
    };
  },
  'SECURITY_ERROR': async () => {
    // Security lockdown
    return {
      action: 'SECURITY_LOCKDOWN',
      executed: true,
      details: 'Security breach detected - system locked down'
    };
  },
  'SYSTEM_ERROR': async () => {
    // System stabilization
    return {
      action: 'SYSTEM_STABILIZATION',
      executed: true,
      details: 'System stability measures activated'
    };
  },
  'API_ERROR': async () => {
    // API failover
    return {
      action: 'API_FAILOVER',
      executed: true,
      details: 'Switched to backup API endpoints where possible'
    };
  }
};

const errorType = errorAnalysis.classification.type;
const actionHandler = criticalActions[errorType];

let immediateResponse = {
  action: 'GENERIC_CRITICAL_RESPONSE',
  executed: false,
  details: 'Critical error acknowledged, manual intervention required'
};

if (actionHandler) {
  try {
    immediateResponse = await actionHandler();
  } catch (actionError) {
    console.error('âŒ Critical Action Execution Failed:', actionError);
    immediateResponse.details += ` - Action execution failed: ${actionError.message}`;
  }
}

// Generate comprehensive critical alert
const criticalAlert = {
  alertId: `critical-${errorAnalysis.errorId}`,
  timestamp,
  severity: 'CRITICAL',
  errorAnalysis,
  immediateResponse,
  status: 'ACTIVE',
  requiresManualIntervention: true,
  affectedSystems: [
    errorType === 'TRADING_ERROR' ? 'Trading Engine' : null,
    errorType === 'SECURITY_ERROR' ? 'Security System' : null,
    errorType === 'API_ERROR' ? 'External APIs' : null,
    errorType === 'SYSTEM_ERROR' ? 'Core System' : null,
    'Monitoring System'
  ].filter(Boolean),
  nextSteps: [
    'Immediate manual review required',
    'Assess system stability',
    'Determine root cause',
    'Implement corrective measures',
    'Monitor for recurrence'
  ],
  escalationPath: [
    'System Administrator',
    'DevOps Team',
    'Security Team (if security-related)',
    'Management (if business impact)'
  ]
};

return [{ json: { criticalAlert } }];

// Automated Recovery Handler
const errorAnalysis = $json.errorAnalysis;
const timestamp = new Date().toISOString();
const retryCount = errorAnalysis.recovery.currentRetryCount + 1;

console.log('ğŸ”„ Automated Recovery Attempt:', {
  errorId: errorAnalysis.errorId,
  retryCount,
  maxRetries: errorAnalysis.recovery.maxRetries,
  strategy: errorAnalysis.recovery.strategy
});

// Recovery strategies implementation
const recoveryStrategies = {
  'RETRY_WITH_FALLBACK': {
    delay: Math.min(errorAnalysis.recovery.retryDelay * Math.pow(2, retryCount - 1), 300000), // Max 5 minutes
    action: 'Exponential backoff retry with fallback endpoints',
    probability: Math.max(0.8 - (retryCount * 0.2), 0.3) // Decreasing success probability
  },
  'RETRY_WITH_CACHE': {
    delay: errorAnalysis.recovery.retryDelay,
    action: 'Retry with cached data fallback',
    probability: 0.7
  },
  'RETRY_ALTERNATIVE_CHANNEL': {
    delay: errorAnalysis.recovery.retryDelay,
    action: 'Switch to alternative communication channel',
    probability: 0.9
  },
  'CLEAN_AND_RETRY': {
    delay: errorAnalysis.recovery.retryDelay,
    action: 'Data cleanup and validation retry',
    probability: 0.8
  },
  'EXPONENTIAL_BACKOFF': {
    delay: Math.min(errorAnalysis.recovery.retryDelay * Math.pow(1.5, retryCount - 1), 180000), // Max 3 minutes
    action: 'Exponential backoff retry',
    probability: Math.max(0.9 - (retryCount * 0.15), 0.2)
  }
};

const strategy = recoveryStrategies[errorAnalysis.recovery.strategy] || {
  delay: 10000,
  action: 'Generic retry',
  probability: 0.5
};

// Simulate recovery attempt (in real implementation, this would execute actual recovery)
const recoveryAttempt = {
  attemptId: `recovery-${errorAnalysis.errorId}-${retryCount}`,
  timestamp,
  retryCount,
  strategy: errorAnalysis.recovery.strategy,
  action: strategy.action,
  delayMs: strategy.delay,
  estimatedSuccess: strategy.probability,
  status: 'INITIATED'
};

// Prepare retry payload for original operation
const retryPayload = {
  ...errorAnalysis.originalError.context,
  retryAttempt: recoveryAttempt,
  originalErrorId: errorAnalysis.errorId,
  retryCount,
  maxRetries: errorAnalysis.recovery.maxRetries,
  recoveryStrategy: errorAnalysis.recovery.strategy
};

// Recovery monitoring data
const recoveryMonitoring = {
  errorPattern: {
    type: errorAnalysis.classification.type,
    frequency: 'Implementation for historical frequency analysis',
    trend: 'Implementation for trend analysis'
  },
  systemHealth: {
    beforeRecovery: 'degraded',
    expectedAfterRecovery: strategy.probability > 0.7 ? 'good' : 'fair',
    monitoringRequired: true
  },
  businessImpact: {
    tradingDisrupted: errorAnalysis.impact.tradingAffected,
    dataLoss: errorAnalysis.impact.dataIntegrityAffected,
    securityRisk: errorAnalysis.impact.securityCompromised,
    estimatedRecoveryTime: `${Math.round(strategy.delay / 1000)} seconds`
  }
};

const recoveryResponse = {
  recoveryAttempt,
  retryPayload,
  monitoring: recoveryMonitoring,
  nextAction: retryCount >= errorAnalysis.recovery.maxRetries ? 'ESCALATE' : 'RETRY',
  scheduledRetryTime: new Date(Date.now() + strategy.delay).toISOString()
};

return [{ json: { recoveryResponse } }];

// Final Recovery Decision
const errorAnalysis = $json.errorAnalysis;
const timestamp = new Date().toISOString();

console.log('â¸ï¸ Recovery Exhausted - Manual Intervention Required:', {
  errorId: errorAnalysis.errorId,
  retriesAttempted: errorAnalysis.recovery.currentRetryCount,
  maxRetries: errorAnalysis.recovery.maxRetries
});

// Generate manual intervention report
const interventionReport = {
  reportId: `intervention-${errorAnalysis.errorId}`,
  timestamp,
  errorSummary: {
    type: errorAnalysis.classification.type,
    priority: errorAnalysis.classification.priority,
    source: errorAnalysis.originalError.source,
    message: errorAnalysis.originalError.message
  },
  recoveryHistory: {
    strategy: errorAnalysis.recovery.strategy,
    attemptsCompleted: errorAnalysis.recovery.maxRetries,
    totalDuration: 'Calculate from first error to final attempt',
    outcome: 'FAILED'
  },
  requiredActions: [
    'Manual diagnosis of root cause',
    'System health assessment',
    'Data integrity verification',
    errorAnalysis.impact.tradingAffected ? 'Trading system review' : null,
    errorAnalysis.impact.securityCompromised ? 'Security audit' : null,
    'Implementation of permanent fix',
    'System monitoring enhancement'
  ].filter(Boolean),
  businessImpact: {
    severity: errorAnalysis.classification.priority,
    affectedSystems: Object.keys(errorAnalysis.impact).filter(key => errorAnalysis.impact[key]),
    estimatedResolutionTime: errorAnalysis.classification.priority === 'CRITICAL' ? '< 1 hour' : 
                           errorAnalysis.classification.priority === 'HIGH' ? '< 4 hours' : '< 24 hours',
    requiresEscalation: true
  },
  recommendations: [
    ...errorAnalysis.recommendations,
    'Implement additional monitoring for this error type',
    'Review and enhance error recovery strategies',
    'Consider system architecture improvements',
    'Update incident response procedures'
  ]
};

return [{ json: { interventionReport } }];

