={{ {
  "body": {
    "type": "SYSTEM_ERROR",
    "priority": $json.errorAnalysis?.classification?.priority || $json.criticalAlert?.severity || "HIGH",
    "title": "System Error Alert: " + ($json.errorAnalysis?.classification?.type || "Unknown Error"),
    "message": $json.errorAnalysis?.originalError?.message || $json.criticalAlert?.immediateResponse?.details || "System error detected",
    "errorId": $json.errorAnalysis?.errorId || $json.criticalAlert?.alertId || "unknown",
    "source": $json.errorAnalysis?.originalError?.source || "error-handler",
    "recoveryStatus": $json.recoveryResponse ? "ATTEMPTED" : ($json.interventionReport ? "MANUAL_REQUIRED" : "CRITICAL"),
    "impact": Object.keys($json.errorAnalysis?.impact || {}).filter(key => $json.errorAnalysis?.impact[key]).join(", ") || "Unknown impact",
    "nextSteps": $json.criticalAlert?.nextSteps || $json.interventionReport?.requiredActions || ["Manual review required"],
    "timestamp": new Date().toISOString()
  }
} }}
